package tesr;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =20;
		int b=40;
		
		int sum = a+b;
		int subt = b-a;
		int mutli = a*b;
		int div =b/a;
		
		int mod = b%a;
		System.out.println("Addition "+sum);
		System.out.println("subtraction "+subt);
		System.out.println("multiplication "+mutli);
		System.out.println("diision "+div);
		
		System.out.println("Moduus "+mod);
		
	}

}
